        params: {
          voiceName: 'ja-JP-KeitaNeural,ja-JP-NanamiNeural', // 语音为多个时随机选择一个
          speed: -4, // 语速范围 -50 到 100，0 为正常语速
        }
      }
    }
    // --- 以下为词典预设项，新增词典参考格式添加 --- //
    const dictOptions = {
      'moji': 'mojisho://?search={query}',                          // MOJi 辞書 [ios|android]
      'mojidict': 'https://www.mojidict.com/searchText/{query}',    // MOJi 辞書网页版 [web]
