      'weblioCJJC': 'https://cjjc.weblio.jp/content/{query}',       // Weblio 日中中日 [web]
      'takoboto': 'https://takoboto.jp/?q={query}',                 // Takoboto [web]
      'mazii': 'https://mazii.net/zh-TW/search/word/jatw/{query}',  // Mazii [web]
