  function getTranslation(key) {
    const translations = {
      feedback: {
        'zh-Hans': '反馈',
        'zh-Hant': '反饋',
      },
      dictionaryTip: {
        'zh-Hans': '未配置适用于此平台的词典',
        'zh-Hant': '未配置適用於此平台的詞典',
      },
      updateReminder: {
        'zh-Hans': '请更新至最新版后再提交反馈',
        'zh-Hant': '請更新至最新版後再提交反饋',
      },
      versionCurrent: {
        'zh-Hans': '当前版本',
        'zh-Hant': '當前版本',
      },
      versionLatest: {
        'zh-Hans': '最新版本',
        'zh-Hant': '最新版本',
      },
      updateDetails: {
        'zh-Hans': '更新说明',
        'zh-Hant': '更新說明',
      },
      cardFeedback: {
        'zh-Hans': '卡片反馈',
